function initialize() {
    this.removeAllMorphs();
    this.pageIndex = 0; // 1-order
    this.pageArray = [];
    this.showBackground = null;
    this.baseBackground = this.owner.pageProto;
    var newBKgnd = this.baseBackground.copy();
    newBKgnd.stack = this;
    this.newPage(newBKgnd);
}

