function showMenu(evt) {
    // menu in page controls <>
    var self = this, items = [];
    items.push(['delete page', function(evt2) {
            self.deletePage()}]);
    items.push(['new background', function(evt2) {
            self.addBackground()}]);

    lively.morphic.Menu.openAt(evt.getPosition(), 'Stack Menu', items);
 }

