function deletePage() {
    // remove the current page
    this.showPageInc(1); // show page after deleted
    if (this.pageIndex - 2 < 0) { // delete last page
        this.pageArray.splice(-1, 1);
    } else {
        this.pageArray.splice(this.pageIndex - 2, 1);
        this.pageIndex = this.pageIndex - 1;
    };
}

