function createAnnotationFromText(textMorph) {
    var string = textMorph.getTextString();

    var splittedString = string.split(":",2);
    if (splittedString.length > 1 && splittedString[0].length < 5) {
        this.annotation.setInitials(splittedString[0]);
        string = string.substring(splittedString[0].length);
    } else {
        this.annotation.setInitials("???");
    }

    this.annotation.setComment(string);
    this.annotation.saveChanges(); // sets author and timestamp
}

