function saveNewComment() {
    var newComment = this.get('inputText').textString,
        lines = newComment.split('\n'),
        firstLine = lines[0],
        listItem = {
            isListItem: true,
            string: firstLine,
            value: {commentText: newComment}
        };
    this.get('commentList').addItem(listItem);
}

