function reverse() {
    // this.reverse()
    var entries = this.getEntries();
    entries.invoke('remove');
    var lastPos = this.get('buttonsPane').bounds().bottomLeft();
    entries.reverse().forEach(function(ea) {
        this.addMorph(ea);
        ea.setPosition(lastPos);
        this.adjustForNewBounds()
        lastPos = ea.bounds().bottomLeft();
    }, this);
}

