function update() {
    module('apps.CouchDB').load(true);
    var buttons = this.get('buttonsPane');
    this.getEntries().invoke('remove');
    var db = new CouchDB(this.getDBName());
    var docs = db.allDocs().rows.collect(
        function (doc) {
            return db.open(doc.id)
        }).select(
        function (doc) {
            return doc.type == 'blog_entry'
        });
    var that = this;
    var foo = docs.select(function (blogEntry) {
        return blogEntry.parent === undefined;
    }).sortBy(function (element) {
        var dateSplit = /^(\d{2})\.(\d{2})\.(\d{4}) (\d{2}):(\d{2}):(\d{2})$/;
        var dateArray = element.publish_date.match(dateSplit);
        return new Date(dateArray[2], dateArray[1] - 1, dateArray[0],
                        dateArray[3], dateArray[4], dateArray[5]);
    });
    // XXX FIXME: order of entries in somehow wrong
    // inspect(foo);
    foo.each(function (blogEntry) {
        var entry = $world.openPartItem('BlogEntry', 'PartsBin/Text');
        entry.setSerializedProperties(blogEntry);
        entry.setPosition($world.getExtent());
        that.addMorph(entry);
        entry.compactView();
        entry.addComments(docs);
    });
    this.addMorph(buttons);
    buttons.setPosition(pt(0,0));
    this.adjustForNewBounds();
}
