function addEntry(doWithEntry) {
    var entry = this,
        comment = this.world().openPartItem("BlogEntry", "PartsBin/Text");

    comment.setLayouter(new lively.morphic.Layout.JournalLayout());
    comment.isJournalEntry = true;
    comment.applyStyle({resizeWidth: true});

    doWithEntry && doWithEntry(comment);

    entry.addMorph(comment);
    comment.setPosition(this.get('buttonsPane').bounds().bottomLeft());
    entry.adjustForNewBounds();
    var parent = entry;
    do {
        parent = parent.owner;
        parent.adjustForNewBounds();
    } while (!parent.isBlog());
}

