function serialize() {
    var serializer = ObjectGraphLinearizer.forNewLivelyCopy()
    var ignoreOwnerPlugin = new GenericFilter();
    ignoreOwnerPlugin.addFilter(function(obj, propName) {
        return propName === 'owner' && this.name == obj.name;
    });
    serializer.addPlugin(ignoreOwnerPlugin);
    this.serializedObject = {
        contents: this.get('entryPane').submorphs.collect(
            function (item) {
                if (item instanceof lively.morphic.Text) {
                    if (item.name == 'title') {
                        return {
                            type: 'title-text',
                            content: item.textString
                        }
                    }
                    return {
                        type: 'text',
                        content: item.textString
                    };
                }
                else if (item instanceof lively.morphic.Box) {
                    return {
                        type: 'media',
                        content: serializer.serialize(item)
                    };
                }
            }),
        parent: this.owner.databaseID,
        author: this.getAuthor(),
        publish_date: this.getPublishDate(),
        type: 'blog_entry',
    };
    if (this.databaseID) {
        this.serializedObject._id = this.databaseID;
    }
    if (this.databaseRev) {
        this.serializedObject._rev = this.databaseRev;
    }
    return this.serializedObject;
}

