function setSerializedProperties(props) {
    var serializer = ObjectGraphLinearizer.forNewLivelyCopy();

    this.databaseID = props._id;
    this.databaseRev = props._rev;
    var entryPane = this.get('entryPane');
    entryPane.submorphs.invoke('remove');
    for (var i = 0; i < props.contents.length; i++) {
        switch (props.contents[i].type) {
            case 'title-text':
                this.addTitleField();
                entryPane.submorphs[i].textString = props.contents[i].content;
                break;
            case 'text':
                this.addTextField();
                entryPane.submorphs[i].textString = props.contents[i].content;
                break;
            case 'media':
                var jso = JSON.parse(props.contents[i].content);
                    item = serializer.deserializeJso(jso);
                item.withAllSubmorphsDo(function() { if (typeof this.setNewId == 'function') this.setNewId(); });
                item.setPosition($world.getExtent());
                entryPane.addMorph(item);
                break;
        }
    }
    this.setPublishDate(props.publish_date);
    this.setAuthor(props.author);
}

