function publish() {
    module('apps.CouchDB').load(true);
    this.compactView();
    var blog = this;
    do {
        blog = blog.owner;
    } while (!blog.isBlog());
    var db = new CouchDB(blog.getDBName());
    this.setPublishDate(new Date().format("dd.mm.yyyy HH:MM:ss"));
    var newtask = this.serialize();
    var result = db.save(newtask);

    if (result.error=="conflict") {
        alert('error while publishing blog entry');
    }
    else {
        this.databaseID = result.id;
        this.databaseRev = result.rev;
        alertOK('blog entry published');
    }
}

