function addComments(docs) {
    var that = this;
    var comments = docs.select(function (blogComment) {
        return that.databaseID === blogComment.parent
    }).sortBy(function(element) {
        var dateSplit = /^(\d{2})\.(\d{2})\.(\d{4}) (\d{2}):(\d{2}):(\d{2})$/;
        var dateArray = element.publish_date.match(dateSplit);
        return new Date(dateArray[2], dateArray[1] - 1, dateArray[0],
                        dateArray[3], dateArray[4], dateArray[5]);
    }).reverse();
    if (comments.length > 0) {
        comments.each(function (blogComment) {
            var comment = $world.openPartItem('BlogEntry', 'PartsBin/Text');
            comment.setSerializedProperties(blogComment);
            comment.setPosition($world.getExtent());
            that.addMorph(comment);
            comment.compactView();
            comment.addComments(docs);
        });
    }
}

