function compactView() {
    var title = this.get('entryPane').get('title');
    if (title &&
        (title.textString == this.defaultTitle() ||
         title.textString == '' &&
         !(this.owner.isBlog()))
        ) {
        title.remove();
    }
    var buttonsPane = this.get('buttonsPane');
    buttonsPane.submorphs.select(
        function(button) {
            return (button.name == 'publishButton' ||
                    button.name == 'addTextButton' ||
                    button.name == 'addMediaButton')
        }).invoke('remove');
    if (this.getAuthor() === $world.getUserName()) {
        var editButton = $world.openPartItem("Button", "PartsBin/Inputs");
        editButton.setLabel("Republish");
        connect(editButton, "fire", this, "republish");
        buttonsPane.addMorph(editButton);
        var addButton = buttonsPane.get("addButton");
        editButton.setPosition(addButton.getPosition().addXY(addButton.getExtent().x, 0).addXY(2,0));
    }
    // this.setPosition(this.owner.getBounds().bottomLeft());
}

