function addTextField(sampleText) {
    textItem = $world.openPartItem('Text', 'PartsBin/Basic/');
    if (sampleText) {
        textItem.textString = sampleText;
    }
    textItem.setFontSize(12);
    var entryPane = this.get('entryPane');
    textItem.setPosition($world.getExtent());
    entryPane.addMorph(textItem);
    textItem.setExtent(
        pt(textItem.owner.getExtent().x-20, 18)
    );
    this.adjustForNewBounds();
    return textItem;
}

