function reset() {
    this.connections = {fromLanguage: {}, toLanguage: {}, textToTranslate: {}, translation: {}};
    this.fromLanguage = 'german';
    this.toLanguage = 'english';
    this.get('fromLanguageList').reset()
    this.get('toLanguageList').reset()
    this.get('input').textString =  'text to translate';
    this.get('result').textString =  'translated text';

}

420.source:
function setupConnections() {
    connect(this.get('fromLanguageList'), 'selection', this, 'fromLanguage');
    connect(this.get('toLanguageList'), 'selection', this, 'toLanguage');
    connect(this.get('translateButton'), 'fire', this.get('input'), 'doSave');
    connect(this.get('input'), 'savedTextString', this, 'textToTranslate');
    connect(this, 'textToTranslate', this, 'translate');
    connect(this, 'translation', this.get('result'), 'textString');
}

423.source:
function translate() {
    this.progressIndicator.appear(100, this);
    this.get('result').textString = '';
    var from = google.language.Languages[this.fromLanguage.toUpperCase()] || '',
        to = google.language.Languages[this.toLanguage.toUpperCase()] || '';
    google.language.translate(
        this.textToTranslate,
        from,
        to,
        function(result) {
            this.progressIndicator.dissolve(100);
            this.translation = result.error ?
                ('Error: ' + result.error.message) :
                Strings.unescapeCharacterEntities(result.translation);
        }.bind(this))
}

