function bootstrap() {
    this.sourceWiki = new URL(this.get('SourceWikiURL').textString);
    this.targetWiki = new URL(this.get('TargetWikiURL').textString);

    this.resetLog();
    this.copyCategory('Basic')
    this.copyCategory('Tools')
    this.copyCategory('Dialogs')
    this.copyCategory('Widgets')
    this.copyCategory('Inputs')

    this.copyDirectory(this.sourceWiki.withFilename("core/apps/"), 
        this.targetWiki.withFilename("apps/"))

    this.copyFiles([this.sourceWiki.withFilename("blank.xhtml").asWebResource()])
}

