function copyFiles(files, sourceURL, targetURL) {
    sourceURL = sourceURL || this.sourceWiki;
    targetURL = targetURL || this.targetWiki;

    var wr = files.pop()
    if (!wr) return;
    var relativePath = wr.getURL().relativePathFrom(sourceURL)
    var content = wr.get().content;
    var fileTargetURL = targetURL.withFilename(relativePath);
    fileTargetURL.asWebResource().put(content);
    this.log("copied " + relativePath 
        + " (" + Math.round(content.length / 1024) +"kb to " + fileTargetURL +")" )
    this.copyFiles.bind(this).curry(files, sourceURL, targetURL).delay(0)
}

