function copyCategory(categoryName) {
    this.log("copy " + categoryName)
    var sourcePartsBinURL = this.sourceWiki.withFilename('PartsBin/')
    var targetPartsBinURL = this.targetWiki.withFilename('PartsBin/')
    var sourceCategoryURL = sourcePartsBinURL.withFilename(categoryName + '/');
    var targetCategoryURL = targetPartsBinURL.withFilename(categoryName + '/');
    targetCategoryURL.asWebResource().ensureExistance()
    var toCopy = sourceCategoryURL.asWebResource().getSubElements(1).subDocuments
    this.copyFiles(toCopy.clone())
}

