function copyDirectory(sourceURL, targetURL) {
    targetURL.asWebResource().ensureExistance()
    var toCopy = sourceURL.asWebResource().getSubElements(1).subDocuments
    this.copyFiles(toCopy.clone(), sourceURL, targetURL)
}

