// MethodFinder (this)

// ** MethodFinder's Scripts **
this.addScript(function browse(fileFragment) {
	fileFragment && fileFragment.browseIt()
});

this.addScript(function reset() {
	this.get('searchResultList').updateList([]);
	this.get('searchResultList').setSelection(null);
	this.get('searchText').setTextString('enter search term here');
});


this.addScript(function search(searchString) {
	var finds = [];
	var re = new RegExp(searchString)
	var i=0;
	var time = Functions.timeToRun(function() {
	Global.classes(true).forEach(function(eaClass) {
		Functions.own(eaClass.prototype)
			.forEach(function(eaMethod) {
				i++;
				if (eaMethod.match(re)){
					finds.push([eaClass, eaMethod])
				}	
			})	
	})})
	alert("search " + i + " methods, found " + finds.length + " methods in " + time + "ms")

	var items = finds.collect(function(ea) {
		return {
			isListItem: true,
			value: ea,
			string: ea[0].name + ": " + ea[1],
		}
	})
	this.get('searchResultList').updateList(items);
});

