function onDrag(evt) {
    var moveDelta = evt.mousePoint.subPt(this.dragStartPoint)

    if (this.isVerticalSpacer()) {
        moveDelta.x = 0;
    } else {
        moveDelta.y = 0;
    }
    if (!this.morphA || !this.morphB) return;

    var newExtent = this.originalMorphAExtent.addPt(moveDelta);
    this.morphA.setExtent(newExtent);

    this.morphB.setPosition(this.originalMorphBPosition.addPt(moveDelta));

    if (this.isVerticalSpacer()) {
        moveDelta.y = - moveDelta.y;
        this.morphB.setExtent(this.originalMorphBExtent.addPt(moveDelta));
        this.align(
            this.bounds().topLeft(),
            this.morphA.bounds().bottomLeft())
    } else {
        moveDelta.x = - moveDelta.x;
        this.morphB.setExtent(this.originalMorphBExtent.addPt(moveDelta));
        this.align(
            this.bounds().bottomLeft(),
            this.morphA.bounds().bottomRight())
    }

}

