function adjustSubmorphExtent() {
    var morphs = this.submorphs.sortBy(function(ea) {
        return ea.bounds().left()
    })
    var total = morphs.inject(0, function(sum, ea) {
        return sum + ea.getExtent().x
    }) 
    var fixed = morphs.select(function(ea) {
        return ea.layout && ea.layout.fixedWidth
    }).inject(0, function(sum, ea) {
        return sum + ea.getExtent().x
    })

    var factor = this.getExtent().x / (total);
    var height = this.getExtent().y

    this.layout

    morphs.each(function(ea) {
        if (ea.layout && ea.layout.fixedWidth) {
             ea.setExtent(pt(ea.getExtent().x, height))
        } else {
             ea.setExtent(pt(ea.getExtent().x * factor, height))
        }
    })
    morphs.inject(pt(0,0), function(last, ea) {
        ea.align(ea.bounds().topLeft(), last)
        return ea.bounds().topRight()
    })
}

