function updateModulesList() {
	var modules = Global.subNamespaces(true).reject(function(ea) { return ea.isAnonymous()})
	var items = modules
		.reject(function(ea) { 
			return (ea.subNamespaces().length == 0) && (ea.classes().length == 0) })
		.pluck('namespaceIdentifier')
		.sort()
		.collect(function(ea){
		var s = ea.replace("Global.", "").replace(/[A-Za-z0-9]+\./g, "  ")
		return {isStringItem: true, value: ea, string: s}
	})

	this.get('modulesList').updateList(items)

}

