function browseClass(classIdentifier) {
	if (! classIdentifier) return;
	this.targetClass = eval(classIdentifier)

	var categories = ["--all--"]
		.concat(Properties.own(this.targetClass.categories))
		.sort();
	var items = categories.collect(function(ea){
		return {isListItem: true, value: ea, string: ea}
	})
	this.get('categoriesList').updateList(items)
	this.get('categoriesList').setSelection("--all--")

	var superClass = this.targetClass.superclass
	var classSource = (superClass.type || superClass.name)
		 + ".subclass('" + this.targetClass.type + "')"
	this.get('sourceText').setTextString(classSource)
        this.get('methodsList').updateList([]);
	this.get('methodsList').setSelection(null)
	this.showMethodPath();
}

