function browseCategory(categoryName) {
	if (!categoryName || !this.targetClass) return;
	var methods;
	if (categoryName == "--all--")
		methods = Functions.own(this.targetClass.prototype);
	else 
		methods = this.targetClass.categories[categoryName];
	var ignore = ['constructor'];
	var items = methods
		.reject(function(ea) { return ignore.include(ea)})
		.collect(function(ea) {
		return {isStringItem: true, value: ea, string: ea}
	})
	this.get('methodsList').updateList(items)
}

