function browseMethod(methodName) {
	if (! methodName || !this.targetClass) return;
	var classObj = this.targetClass;
	var methodName = methodName;

	var source = "// changes here only affect runtime\n" +
		classObj.type + ".addMethods({\n\t" + methodName + ":" +
		classObj.prototype[methodName].getOriginal() + "\n});"
	this.get('sourceText').setTextString(source)

	this.showMethodPath();
}

