function searchClass(className) {
	this.reset();
        if (! className) {
            this.updateModulesList()
            return 
        }
	this.get('searchText').setTextString(className);
	this.get('modulesList').updateList([{
		isListItem: true, string: "class search" , value: false}])

	var classes = Global.classes(true)
		.select(function(ea) { return ea.type && ea.type.search(className) != -1 })
		.uniq()
		.sortBy(function(ea) { return ea.name})
	this.updateClasses(classes)
}

