function drawCrossAtSatBrt(sat, brt) {
    var ctx = this.getContext(),
        extent = this.getExtent(),
        height = extent.y,
        width = extent.x,
        x = Math.round(width * sat) + 0.5,
        y = Math.round(height * (1-brt)) + 0.5,
        lineLength = 5,
        margin = 2;
    //console.log(y);
    this.clear();
    if (ctx) {
        ctx.beginPath();
        var line = function(x1, y1, x2, y2) {    
            ctx.moveTo(x1, y1);
            ctx.lineTo(x2, y2);
            ctx.lineWidth = 0.5;
            ctx.strokeStyle = 'black';
            ctx.stroke();
        }

        line(x, y - margin, x, y - margin - lineLength);
        line(x, y + margin, x, y + margin + lineLength);
        line(x- margin, y, x - margin - lineLength, y);
        line(x + margin, y, x + margin + lineLength, y);
    }
}

