function showFileContents(path) {
    // path ='/texte/other/begr.txt'
    path = path.replace(/^\//, "")

    if (path.match(/\.txt$/)) {
        alertOK("show " + path)
        this.showInNewTextMorph(this.db.getFile(path))
    }
    if (path.match(/\.itmz$/)) {
        var url = URL.makeProxied(this.db.getFileURL(path.replace(/ /g,"%20")))
        this.showInNewTextMorph("loading " + path)
        var done = function(zip) {
            var contents = zip.entries[0].extract(undefined, true)
            this.showInNewTextMorph(print_topic_tree(extractTopics(contents)))
            alertOK("done loading " + (contents.length / 1024) + "KB")
        }.bind(this)
        var zip = new ZipFile("" + url, done)
    }
}

