function showDirectory(meta, filename) {
    this.get('FileList').clearSelections();
    this.get('FileContents').removeAllMorphs()    
    var parentFilename = filename.replace(/\/?[^/]*?$/,"")
    var p = this.db.getMetadata(parentFilename)
    var files = [{string: '.', value: meta}, 
        {string: '..', value: p}];
    if (meta.contents)
        files.pushAll(meta.contents.collect(function(ea) {
            var re = new RegExp("\/?" + filename + "\/?", "i") 
            return { 
                string: ea.path.replace(re, "") + (ea.is_dir ? "/" : ""), 
                value: ea }
        }))
    this.get('FileList').setList(files)
}

