function showInNewTextMorph(string) {
    var container = this.get('FileContents')
    container.removeAllMorphs()
    var text = new lively.morphic.Text(pt(0,0).extent(container.getExtent()), "") ;
    text.textString = string;
    text.applyStyle({
        clipMode: 'auto',
        fixedWidth: true, fixedHeight: true,
        resizeWidth: true, resizeHeight: true})
    container.addMorph(text)
}

