function updateSourceFromErrorLine(errorLine) {

    var fileFragment = errorLine.fileFragment();

    var sourceText  = this.get('SourceText');
    sourceText.setTextString(fileFragment.getSourceCode())

    var from = fileFragment.charsUpToLine(errorLine.line) + errorLine.linePosition -  1
    var to = fileFragment.charsUpToLine(errorLine.line + 1) - 1; // line end
    sourceText.emphasize({backgroundColor: Color.gray}, from, to)

}

