function renderTree(data) {
    this.rootNode = this.createTreeNode('Properties', data);
    this.addMorph(this.rootNode);
     
    var wrapper = this.createTreeWrapper(this.rootNode);
    var scroll = this.createScrollClip(wrapper);

    this.addMorph(scroll);

    this.startStepping(500/*ms*/, 'update');  
}

