function createTreeNode(label, data, level, parent) {
    level = level || 0;

    // create morph / layout
    var treeNode = this.createTreeNodeMorph(data, level);

    // add scripts
    this.addScriptsToTreeNode(treeNode);

    // initialization
    treeNode.data = data;
    treeNode.propertyList = [].pushAll(Properties.own(treeNode.data));
    treeNode.level = level;
    treeNode.treeView = this;
    treeNode.label = label;
    treeNode.children = [];
    treeNode.parent = (parent ? parent : null);

    var expander = this.createExpanderButton(treeNode);
    treeNode.addMorph(expander);

    var text = this.createPropertyText(treeNode);
    treeNode.addMorph(text);


    return treeNode;
}

