function createTreeWrapper(node) {

    var rectangleMorph = new lively.morphic.Shapes.Rectangle(this.sizes.wrapperExtent);
    var wrapper = new lively.morphic.Morph(rectangleMorph);

    wrapper.addScript(function getLayoutableSubmorphs() {
        return this.submorphs.reject(function(ea) {
            return (ea.isEpiMorph || ea.isBeingDragged || !ea.isVisible());
        });
    });

    var layouter = new lively.morphic.Layout.JournalLayout(wrapper);
    var superBasicLayout = layouter.basicLayout;
    layouter.basicLayout = function(container, submorphs) {
        submorphs.sortBy = function() { return this };
        superBasicLayout.bind(layouter)(container, submorphs);
        delete submorphs.sortBy;
    };
    layouter.borderSize = 0;
    layouter.spacing = 0;
    wrapper.setLayouter(layouter);
    wrapper.layout.resizeWidth = true;

    if (node)
        wrapper.addMorph(node);

    return wrapper;
}

