function createExpanderButton(treeNode) {

    var expanderPositionX = this.sizes.expanderOffset.x + this.sizes.levelIndent * treeNode.level;
    var expanderPosition = pt(expanderPositionX, this.sizes.expanderOffset.y);
    var expander = new lively.morphic.Box(expanderPosition.extent(this.sizes.expanderExtent));

    // layout
    expander.setBorderWidth(1);
    expander.setBorderColor(this.colors.border);
    expander.disableGrabbing();
    expander.disableDragging();
    treeNode.expander = expander;

    // add scripts
    this.addScriptsToExpander(expander);

    // initialization
    expander.isExpanded = false;
    treeNode.updateExpanderColor();

    return expander;
}

