function createPropertyText(treeNode) {

    var textPosition = pt(this.sizes.textOffset.x + this.sizes.levelIndent * treeNode.level, this.sizes.textOffset.y);

    var textMorph = new lively.morphic.Text(textPosition.extent(pt(0, 0)), "");  
    this.addScriptsToText(textMorph);
    textMorph.layoutText(this.fonts);

    treeNode.text = textMorph;
    treeNode.displayValue();

    return textMorph;
}

