function createScrollClip(wrapper) {
    var scroll = new lively.morphic.Clip(this.getExtent().extentAsRectangle());

    // layout
    scroll.layout = {
        resizeWidth: true,
        resizeHeight: true
    };
    scroll.disableGrabbing();
    scroll.disableDragging();
    scroll.setName('TreeViewScrollPane');

    scroll.addScript(function setClipModeHTML(ctx, modeString) {
        $super(ctx, modeString);
        ctx.morphNode.style['overflow-x'] = 'hidden';
    });

    scroll.addMorph(wrapper);
    return scroll;
}

