function addScriptsToTreeNode(treeNode) {

    treeNode.addScript(function updateExpanderColor() {
        var colors = this.treeView.colors;
        var fillColor;
        if (this.expander.isExpanded || !(Objects.isMutableType(this.data) && Properties.own(this.data).length > 0)) { 
            fillColor = colors.expandedExpander;
        } else {
            fillColor = colors.contractedExpander;
        }
        this.expander.setFill(fillColor);
    });

    treeNode.addScript(function onMouseDown() {
        this.treeView.owner.moveFocusTo(this);
    });

    treeNode.addScript(function isRoot() {
        return (this.parent === null || this.parent === undefined);
    });

    treeNode.addScript(function displayValue() {
        // set label
        var labelString = this.treeView.getDisplayStringFor(this);
        if (labelString.length > 100) labelString = labelString.substring(0, 100) + '...';
        this.text.setTextString(labelString);

        // reset the bounds
        var fontSize = this.treeView.fonts.size;
        var textExtent = pt(labelString.length * fontSize * (3 / 4), fontSize * 2);
        this.text.setExtent(textExtent);

        // emphasize the properties current value
        this.text.emphasize({
            color: this.treeView.colors.emphasizedText,
            italics: 'italic'
        }, this.label.length, this.text.textString.length);
    });

    treeNode.addScript(function update() {

        // if actual value of the represented property changed (reference changed)
        if (this.parent && this.data !== this.parent.data[this.label]) {
            this.data = this.parent.data[this.label];
            this.displayValue();
        }

        if (Objects.typeStringOf(this.data) === 'String') return;

        // when properties have changed (same object, but other properties)
        var allProperties = Properties.own(this.data);
        var that = this;
        var allPropertiesKnown = allProperties.every(function (each) {
            return that.propertyList.indexOf(each) !== -1;
        })
        if (!allPropertiesKnown || allProperties.length !== this.propertyList.length) {
            this.updateExpanderColor();
            this.displayValue();

            // if expanded, redraw children
            if (this.expander.isExpanded) {
                this.expander.isExpanded = false;
                this.expander.toggle();
            }

            this.propertyList = allProperties;
        }

        if (Objects.isMutableType(this.data) && Properties.own(this.data).length > 0) {
            this.children.forEach(function (each) {
                each.update()
            });
        }
    });

}

