function addScriptsToExpander(expander) {

    expander.addScript(function toggle() {
        // asynchronous toggling
        (function () { this.toggleExpansion(); }).bind(this).delay(0);
    });

    expander.addScript(function toggleExpansion() {
        var treeNode = this.owner;
        var treeView = treeNode.treeView;

        if (!this.isExpanded && Objects.isMutableType(treeNode.data) && Properties.own(treeNode.data).length > 0) {
            this.isExpanded = true;

            if (treeNode.childrenWrapper) {
                treeNode.childrenWrapper.setVisible(true);
            } if (treeNode.childrenWrapper && treeNode.children.length !== Properties.own(treeNode.data)) {
                treeNode.children = [];
                treeNode.childrenWrapper.removeAllMorphs();
                treeNode.childrenWrapper.addTreeNodesForProperties(treeNode);
            } else {
                if (Objects.isMutableType(treeNode.data)) {
                    var wrapper = treeView.createTreeWrapper();

                    wrapper.addScript(function addTreeNodesForProperties(treeNode) {
                        var that = this;
                        var props = Properties.own(treeNode.data);
                        if (Objects.typeStringOf(treeNode.data) !== 'Array') props = props.sort();
                        props.forEach(function(prop) {
                            (function() {
                                var childNode = treeNode.treeView.createTreeNode(prop, treeNode.data[prop], treeNode.level + 1, treeNode);
                                treeNode.children.push(childNode);
                                var nodeWrapper = treeNode.treeView.createTreeWrapper(childNode);
                                that.addMorph(nodeWrapper);
                            }).bind(this).delay(0);    // asynchronous
                        });
                    });
                    wrapper.addTreeNodesForProperties(treeNode);

                    treeNode.childrenWrapper = wrapper;
                    treeNode.owner.addMorph(wrapper);
                } 
            }
        } else {
            this.isExpanded = false;
            treeNode.childrenWrapper && treeNode.childrenWrapper.setVisible(false);
        }
        treeNode.updateExpanderColor();
        treeNode.owner.applyLayout();
    });

}

