function getDisplayStringFor(treeNode) {
    var typeInfo = '';
    if (treeNode.data && treeNode.data.name) {
        typeInfo = treeNode.data.name;
    } else {
        typeInfo = (Objects.isMutableType(treeNode.data) ? 
            Objects.shortPrintStringOf(treeNode.data) : treeNode.data);
    }
    return treeNode.label + ": " + typeInfo;
}

