function toggleAll() {

    if (!this.rootNode) return;

    try {
        JSON.stringify(this.rootNode.data);
    } catch (e) {
        // probably a circular structure
        return;
    }

    var toggleChildren = function (node) {

        if (!node.children) return;

        (function() {
            node.children.forEach(function (each) {
                each.expander.toggleExpansion();
                toggleChildren(each);
            });
        }).bind(this).delay(0);    // asynchronous
    }

    toggleChildren(this.rootNode);

}

