function moveFocusTo(focusedNode) {

    // set workspace context
    var previouslyFocusedNode = this.focusedNode;
    this.focusedNode = focusedNode;
    this.workspace.doitContext = this.focusedNode.data;

    // display focus
    if (previouslyFocusedNode && previouslyFocusedNode.setFill) 
        previouslyFocusedNode.text.setFill(null);
    this.focusedNode.text.setFill(this.focusedNode.treeView.colors.focusedText);
    this.focusedNode.expander.toggle();
}

