function onLoad() {
    var str = "<iframe height=\"100%\" width=\"100%\" ";
    str += "style=\"overflow:auto;height:100%;width:100%\" ";
    str += "src=\"" + this.url + "\" ";
    str += "scrolling=\"yes\" frameborder=\"0\"></iframe>";
    this.jQuery().html(str);
    var first = true;
    this.frame().onload = function () {
        if (first) {return first = false;}
        var document = this.frame().contentDocument;
        lively.bindings.signal(this, "location", {
            title: document ? document.title : "Web Browser", 
            url: document ? document.location.href : this.frame().src});
    }.bind(this);
}

