function navigateTo(url) {
    // this.navigateTo("..")
    if (!url) return;
    this.url = url;
    var document = this.frame().contentDocument;
    var current = document && document.location.href;
    var src = this.frame().src;
    if ((current && url != current) || (!current && url != src)) {
        this.frame().src = url;
    }
}

