function displayPage(url) {
    this.targetURL = url;
    if (url.isLeaf()) {
        this.get('BrowserWindow').navigateTo(url+"?askBeforeQuit=false");
        this.get('BrowserWindow').setVisible(true)
        this.updateVersions()
    } else {
        this.get('BrowserWindow').setVisible(false)
        this.get('VersionsList').setList([])
        this.updateLinks()
    }
}

