function deletePage() {
    var url  = new URL(this.get("List").selection);
    this.world().confirm('Do you really want to delete ' + url.filename() + '?',   
        function(answer) {
            if (!answer) return;
            new WebResource(url)
                .statusMessage('Removed ' + url, 'Error removing ' + url, true)
                .del();
            this.updateLinks();
        }.bind(this))

}

