function addItem(item) {
    if (this.itemList.length == 0) {
        this.updateList(['begin', 'end'])
        this.selectAt(1)
    }

    if (!this.isBlocked) {
        var sel = this.getSelectedIndexes().first();
        if (sel == 0)
            sel = 1
        var end = this.itemList.splice(sel);
        this.itemList.push(item)
        this.updateList(this.itemList.concat([end.last()]));
    }
}

