function onFire() {
    this.owner.globalLogger.isBlocked = true;

    var list = this.owner.globalLogger;
    var idx = list.getSelectedIndexes()[0];
    if (idx<list.itemList.length-1) {
        list.itemList[idx].redo && list.itemList[idx].redo()
        list.selectAt(idx+1)
    }

    this.owner.globalLogger.isBlocked = false;
}

