function reset() {
    lively.morphic.World.current().globalLogger = this.owner.globalLogger
    this.owner.globalLogger.updateList([])
    this.owner.globalLogger.isBlocked = false
    lively.morphic.World.current().withAllSubmorphsDo(function (ea) {
        ea.disableLogging()
    })
}
